import React, { useEffect, useState } from 'react';

// E2E gating overlay to block user interactions until the test explicitly
// signals it is ready. This prevents header or route changes from other
// app flows from racing with seeded test state.
const E2EGate: React.FC = () => {
  const [isE2E, setIsE2E] = useState(false);
  const [isReady, setIsReady] = useState(false);

  useEffect(() => {
    try {
      // Consider tests present if we have an explicit init or sticky view
      const seeded = !!(window as any).__WONKY_TEST_INITIALIZE__ || !!(window as any).__WONKY_TEST_STICKY_VIEW__;
      setIsE2E(seeded);
      const initialReady = !!(window as any).__WONKY_TEST_READY__ || (window as any).appState?.view === 'game-master-dashboard';
      setIsReady(initialReady);

      const handle = () => setIsReady(!!(window as any).__WONKY_TEST_READY__ || (window as any).appState?.view === 'game-master-dashboard');
      // expose a global setter to allow tests to release the gate
      try { (window as any).__WONKY_TEST_RELEASE_GATE__ = () => { (window as any).__WONKY_TEST_READY__ = true; handle(); }; } catch (e) { /* ignore */ }

      // Listen to a global property change via polling - cheap and fine for E2E
      const interval = setInterval(() => handle(), 200);
      return () => clearInterval(interval);
    } catch (e) {
      setIsE2E(false);
      setIsReady(true);
    }
  }, []);

  // Don't render anything outside E2E runs or if the test has already released the gate
  if (!isE2E || isReady) return null;

  return (
    <div data-testid="e2e-gate" className="fixed inset-0 z-[9999] bg-black/5 pointer-events-auto flex items-center justify-center">
      <div className="bg-black/60 text-white p-3 rounded-md">
        <strong>E2E initialization gate</strong>
        <div className="opacity-90 mt-2">
          Tests are preparing the seeded state ΓÇö waiting for <code>__WONKY_TEST_READY__</code>
        </div>
      </div>
    </div>
  );
};

export { E2EGate };
export default E2EGate;

import React, { useEffect, useState } from 'react';

export default function GoogleSignInButton() {
  const [profile, setProfile] = useState<any>(null);

  const loadProfile = async () => {
    try {
      const resp = await fetch('/api/google/me');
      if (resp.status === 200) {
        const json = await resp.json();
        setProfile(json.profile);
      } else {
        setProfile(null);
      }
    } catch (e) {
      setProfile(null);
    }
  };

  useEffect(() => { loadProfile(); }, []);

  const signIn = () => {
    // Redirect to server-side auth route
    window.location.href = '/api/google/auth';
  };

  const signOut = async () => {
    await fetch('/api/google/logout', { method: 'POST' });
    setProfile(null);
  };

  if (profile) {
    return (
      <div className="flex items-center gap-3">
        <img src={profile.picture} alt={profile.name} className="h-8 w-8 rounded-full" />
        <div className="font-mono text-sm">{profile.name}</div>
        <button onClick={signOut} className="ml-2 px-2 py-1 text-xs bg-surface-700 rounded">Sign out</button>
      </div>
    );
  }

  return (
    <button onClick={signIn} className="px-4 py-2 bg-accent-blue text-white rounded">Sign in with Google</button>
  );
}

import React, { useState } from 'react';
import GoogleSignInButton from './GoogleSignInButton';

export default function GoogleWorkspaceShowcase() {
  const [demoData, setDemoData] = useState<any>(null);
  const runDemo = async () => {
    try {
      const resp = await fetch('/api/google/demo');
      const json = await resp.json();
      setDemoData(json);
    } catch (e) {
      setDemoData({ error: String(e) });
    }
  };

  return (
    <div className="card-base p-4">
      <div className="flex justify-end mb-2">
        <GoogleSignInButton />
      </div>
      <h2 className="text-xl font-bold">Google Workspace Showcase</h2>
      <p className="text-sm text-text-muted">Demonstrates Calendar, Drive, Sheets, and Gmail orchestrations via server-side routes.</p>
      <div className="mt-4">
        <button onClick={runDemo} className="px-4 py-2 bg-accent-blue text-white rounded">Run Demo</button>
      </div>
      <div className="mt-6">
        <h3 className="text-md font-semibold">What this does</h3>
        <ul className="list-disc list-inside">
          <li>Simulates Calendar event creation</li>
          <li>Uploads a SOP to Drive (sample)</li>
          <li>Appends weekly-review data to a Sheet</li>
          <li>Sends a mail via Gmail using a template</li>
        </ul>
      </div>
      {!!demoData && (
        <div className="mt-6 card-base p-4 bg-surface-700">
          <h3 className="text-md font-semibold">Demo result</h3>
          {demoData.error && <div className="text-red-400">Error: {demoData.error}</div>}
          {demoData.profile && (
            <div className="flex items-center gap-3 mt-2">
              <img src={demoData.profile.picture} alt={demoData.profile.name} className="h-8 w-8 rounded-full" />
              <div className="text-sm">Signed in as <strong>{demoData.profile.name}</strong> ({demoData.profile.email})</div>
            </div>
          )}
          {Array.isArray(demoData.events) && (
            <div className="mt-3">
              <h4 className="font-semibold">Upcoming Events</h4>
              <ul className="list-disc list-inside">
                {demoData.events.map((ev: any) => (
                  <li key={ev.id || ev.summary}>{ev.summary} ΓÇö {String(ev.start?.dateTime || ev.start?.date || '')}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useOscilloscope } from '@contexts/OscilloscopeContext';
import { getGrandmaAdvice, getGrandpaAdvice, getBobAdvice, getMargeAdvice } from '../services/geminiService';
import { sanitizePrompt } from '../utils/sanitizePrompt';
import { Briefcase, Gavel, Hammer, Heart } from 'lucide-react'; // Example Lucide icons

// --- Persona Data (Matching Context Keys) ---
const PERSONA_UI = {
  grandma: { key: 'grandma', icon: '≡ƒæ╡', helper: getGrandmaAdvice, color: 'text-accent-pink', role: 'Structural Fix' },
  grandpa: { key: 'grandpa', icon: '≡ƒ¢á∩╕Å', helper: getGrandpaAdvice, color: 'text-accent-teal', role: 'Systems Integrity' },
  bob: { key: 'bob', icon: 'ΓÜí∩╕Å', helper: getBobAdvice, color: 'text-yellow-400', role: 'Kinetic Coach' },
  marge: { key: 'marge', icon: '≡ƒÆû', helper: getMargeAdvice, color: 'text-purple-400', role: 'Empathetic Planner' },
};

export const GrandmaHelper: React.FC = () => {
  // II. ARCHITECTURAL MANDATES: Unify Context
  const { state, dispatch, getPersonaName, getPersonaRole } = useOscilloscope();
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [redactedWarning, setRedactedWarning] = useState(false);

  const activePersona = PERSONA_UI[state.activePersona];

  const handleAsk = async (userQuery: string) => {
    if (isLoading || userQuery.trim() === '') return;

    setIsLoading(true);
    setResponse('');
    setRedactedWarning(false);

    // III. FILE-SPECIFIC CLEANUP: PII Sanitization
    const { cleanedQuery, redacted } = sanitizePrompt(userQuery);
    if (redacted) setRedactedWarning(true);

    try {
      // Use the helper function associated with the active persona
      const advice = await activePersona.helper(cleanedQuery);
      setResponse(advice);
    } catch (error) {
      console.error('LLM Call Failed:', error);
      // Ensure local fallback uses the required persona ending
      setResponse(`Communication array offline. Please check network. ${activePersona.key === 'grandma' ? 'Love, Grandma.' : 'ΓÇö' + activePersona.key + '.'}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handlePersonaChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    dispatch({ type: 'SET_PERSONA', payload: e.target.value as 'grandma' });
  };

  const handleQuickAction = () => {
    const quickQuery = "I feel stuck and overwhelmed, what is my first step?";
    setQuery(quickQuery);
    handleAsk(quickQuery);
  };

  return (
    <motion.div 
      className="card-base p-6 space-y-4"
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      {/* HEADER & AVATAR (Dynamic Persona Display) */}
      <div className="flex items-center gap-4">
        <div className={`text-4xl ${activePersona.color}`} data-testid="persona-avatar">{activePersona.icon}</div>
        <div className="flex-1">
          <h3 className={`text-xl font-bold uppercase ${activePersona.color}`} data-testid="persona-display-name">
            ASK {getPersonaName(state.activePersona)}
          </h3>
          <p className="text-text-muted text-xs">
            {getPersonaRole(state.activePersona)} ({activePersona.key})
          </p>
        </div>

        {/* Persona Selector */}
        <select
          value={state.activePersona}
          onChange={handlePersonaChange}
          className="input-base text-sm py-1"
          aria-label="AI persona selector"
          data-testid="persona-select"
          disabled={isLoading}
        >
          {Object.entries(PERSONA_UI).map(([key, persona]) => (
            <option key={key} value={key}>
              {getPersonaName(key)}
            </option>
          ))}
        </select>
      </div>

      {/* INPUT AREA */}
      <div className="flex gap-2 relative">
        <input
          type="text"
          placeholder={`What's overwhelming you, ${activePersona.key}?`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={(e) => { if (e.key === 'Enter') handleAsk(query); }}
          className="input-base flex-1"
          data-testid="grandma-input"
          disabled={isLoading}
        />
        <button
          onClick={() => handleAsk(query)}
          className={`btn-primary ${isLoading ? 'opacity-50' : ''}`}
          data-testid="grandma-ask-button"
          disabled={isLoading}
        >
          {isLoading ? 'TRANSMITTING...' : 'ASK'}
        </button>
      </div>
      
      {/* Quick Action Button (Lemon CTA) - Now uses the FAB utility class */}
      <motion.div
        className="fab p-2 text-lg absolute bottom-0 right-0 transform translate-x-1/2 translate-y-1/2"
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
      >
          <button 
              onClick={handleQuickAction}
              className="w-full h-full bg-transparent border-none text-white focus:outline-none"
              title="Quick Stuck Action"
              data-testid="quick-stuck-action"
          >
              ≡ƒìï
          </button>
      </motion.div>


      {/* RESPONSE AREA */}
      {response && (
        <motion.div
          className="card-inner mt-4 border-accent-teal/50"
          initial={{ opacity: 0, scale: 0.98 }}
          animate={{ opacity: 1, scale: 1 }}
          data-testid="grandma-output"
        >
          {redactedWarning && (
            <p className="text-accent-pink text-xs mb-2">
              ΓÜá∩╕Å PII detected and sanitized before transmission.
            </p>
          )}
          <p className="text-text-light whitespace-pre-wrap">{response}</p>
        </motion.div>
      )}

      {/* Loading Indicator */}
      {isLoading && (
        <div className="text-center py-2 text-accent-teal">
          Calculating Trajectory...
        </div>
      )}
    </motion.div>
  );
};

import React from 'react';

type AdviceProps = {
  task?: string;
};

function makeGrandpaAdvice(task = 'this task') {
  // Grandpa: calm, practical, anecdotal, timeboxed guidance
  return `If I were you, I'd start with the simplest part of ${task} ΓÇö just one tiny step. Set a 15-minute timer and do nothing else. After that, reassess. If it went well, do the next tiny step. If not, take a break and try again later.`;
