const BioHacksView: React.FC = () => {
  const sensoryRecipes = [
    {
      title: '≡ƒìï The Lime Drag',
      description: 'A sharp, sour taste to jolt focus and provide a sensory shock, ideal for breaking through mental fog or overwhelming moments.',
      benefits: ['Rapid Focus', 'Sensory Shock', 'Mental Clarity'],
      ingredients: ['Fresh Lime', 'Salt (optional)'],
      instructions: [
        'Cut a fresh lime into wedges.',
        'Take a deep breath.',
        'Suck on a lime wedge, allowing the intense sourness to flood your senses.',
        'Optionally, sprinkle a little salt on the lime for an extra jolt.',
        'Focus on the sensation, letting it reset your mental state.'
      ],
      color: 'bg-yellow-500/20 text-yellow-300 border-yellow-500'
    },
    {
      title: '≡ƒò╢∩╕Å The Shield',
      description: 'Utilize deep pressure and visual dampening to create a personal sensory shield, perfect for reducing overwhelm in chaotic environments.',
      benefits: ['Overwhelm Reduction', 'Sensory Dampening', 'Calm'],
      ingredients: ['Weighted Blanket or Heavy Object', 'Eye Mask or Dark Glasses', 'Noise-Cancelling Headphones (optional)'],
      instructions: [
        'Find a quiet space if possible.',
        'Apply a weighted blanket or heavy object across your lap or chest for deep pressure input.',
        'Put on an eye mask or dark glasses to reduce visual stimuli.',
        'Optionally, wear noise-cancelling headphones for auditory dampening.',
        'Focus on your breath and the feeling of pressure, creating a personal calm zone.'
      ],
      color: 'bg-blue-500/20 text-blue-300 border-blue-500'
    },
    {
      title: '≡ƒº▒ The Heavy Work',
      description: 'Engage in strenuous physical activity to channel anxiety and promote grounding, effective for emotional regulation and re-centering.',
      benefits: ['Grounding', 'Anxiety Relief', 'Emotional Regulation'],
      ingredients: ['Heavy Backpack or Resistance Bands', 'Physical Space'],
      instructions: [
        'Fill a backpack with heavy items (books, water bottles) or grab resistance bands.',
        'Perform pushing, pulling, or lifting movements against the resistance for 5-10 minutes.',
        'Focus on the physical exertion and the sensation of your muscles working.',
        'Alternatively, do wall push-ups, carry heavy bags, or engage in other heavy work activities.',
        'Notice how your body feels grounded and your mind becomes clearer.'
      ],
      color: 'bg-red-500/20 text-red-300 border-red-500'
    },
  ];

  return (
    <div className="min-h-screen bg-sanctuary-bg text-sanctuary-text-main p-6">
      <header className="text-center mb-10">
        <h1 className="text-4xl font-bold text-primary-400 mb-2">≡ƒÆè The Apothecary</h1>
        <p className="text-text-muted text-lg">Bio-Hacks for Neurodivergent Regulation</p>
        <p className="text-sm text-gray-500 mt-2">Sensory recipes for focus, calm, and grounding.</p>
      </header>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-7xl mx-auto">
        {sensoryRecipes.map((recipe, index) => (
          <div key={index} className={`bg-card-dark rounded-xl shadow-lg p-6 border ${recipe.color}`}>
            <h2 className="text-2xl font-bold mb-3">{recipe.title}</h2>
            <p className="text-text-muted mb-4">{recipe.description}</p>
            
            <div className="mb-4">
              <h3 className="text-lg font-semibold mb-2">Benefits:</h3>
              <ul className="list-disc list-inside text-sm text-gray-400">
                {recipe.benefits.map((benefit, bIndex) => (
                  <li key={bIndex}>{benefit}</li>
                ))}
              </ul>
            </div>

            <div className="mb-4">
              <h3 className="text-lg font-semibold mb-2">Ingredients:</h3>
              <ul className="list-disc list-inside text-sm text-gray-400">
                {recipe.ingredients.map((ingredient, iIndex) => (
                  <li key={iIndex}>{ingredient}</li>
                ))}
              </ul>
            </div>

            <div>
              <h3 className="text-lg font-semibold mb-2">Instructions:</h3>
              <ol className="list-decimal list-inside text-sm text-gray-400 space-y-1">
                {recipe.instructions.map((instruction, inIndex) => (
                  <li key={inIndex}>{instruction}</li>
                ))}
              </ol>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BioHacksView;

import React, { createContext, useContext, useReducer, ReactNode } from 'react';

// --- TYPES ---
type PersonaKey = 'grandma' | 'grandpa' | 'bob' | 'marge';
type FocusMode = 'scout' | 'laser' | 'recovery' | 'none';

interface ProfileStack {
  id: string;
  name: string;
  persona: string;
  createdAt: string;
  // Add other required fields...
}

interface NeuroPrefs {
  simplifiedUi: boolean;
  reduceAnimations: boolean;
  // Add other neuro-specific prefs
}

interface OscilloscopeState {
  // Canonical State Fields
  activePersona: PersonaKey;
  focusMode: FocusMode;
  isAirlockOpen: boolean;
  isSourMode: boolean;
  
  // Decoupled Data References
  profileStacks: ProfileStack[];
  neuroPrefs: NeuroPrefs;
  
  // Status Tracking
  completedTaskCount: number;
}

type Action =
  | { type: 'SET_FOCUS_MODE'; payload: FocusMode }
  | { type: 'SET_PERSONA'; payload: PersonaKey }
  | { type: 'TOGGLE_AIRLOCK'; payload: boolean }
  | { type: 'TOGGLE_SOUR_MODE' }
  | { type: 'INCREMENT_TASK_COUNT' }
  | { type: 'SET_STATE'; payload: Partial<OscilloscopeState> }; // Generic state setter

// --- PERSONA MAPPING (Yin/Yang Principle) ---
const PERSONA_MAP = {
  // YIN / Stability (The Katen Legacy)
  grandma: { key: 'grandma', display: 'Margie Fay Katen', role: 'Homemaker' },
  grandpa: { key: 'grandpa', display: 'Robert Katen', role: 'Mechanic' },
  // YANG / Kinetic Energy (The Bob & Marge Protocol)
  bob: { key: 'bob', display: 'Bob Haddock', role: 'Kinetic Coach' },
  marge: { key: 'marge', display: 'Marge Watson', role: 'Planner' },
};

// --- INITIAL STATE ---
const initialState: OscilloscopeState = {
  activePersona: 'grandma',
  focusMode: 'none',
  isAirlockOpen: false,
  isSourMode: false,
  profileStacks: [
    { id: 'racecar-1', name: 'Race Car Flow', persona: 'william', createdAt: new Date().toISOString() }
  ] as ProfileStack[],
  neuroPrefs: { simplifiedUi: false, reduceAnimations: false },
  completedTaskCount: 0,
};

// --- REDUCER ---
const reducer = (state: OscilloscopeState, action: Action): OscilloscopeState => {
  switch (action.type) {
    case 'SET_FOCUS_MODE':
      return { ...state, focusMode: action.payload };
    case 'SET_PERSONA':
      return { ...state, activePersona: action.payload };
    case 'TOGGLE_AIRLOCK':
      return { ...state, isAirlockOpen: action.payload };
    case 'TOGGLE_SOUR_MODE':
      // CRITICAL CSS FIX: Use browser API to enforce global theme change
      document.body.classList.toggle('sour-mode', !state.isSourMode);
      return { ...state, isSourMode: !state.isSourMode };
    case 'INCREMENT_TASK_COUNT':
      return { ...state, completedTaskCount: state.completedTaskCount + 1 };
    case 'SET_STATE':
        return { ...state, ...action.payload };
    default:
      return state;
  }
};

// --- CONTEXT & HOOK ---
const OscilloscopeContext = createContext<{
  state: OscilloscopeState;
  dispatch: React.Dispatch<Action>;
  getPersonaName: (key: PersonaKey | string) => string;
  getPersonaRole: (key: PersonaKey | string) => string; // Added utility
} | undefined>(undefined);

export const OscilloscopeProvider = ({ children }: { children: ReactNode }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  // Utility Function: getPersonaName (used by GrandmaHelper)
  const getPersonaName = (key: PersonaKey | string): string => {
    return PERSONA_MAP[key as PersonaKey]?.display || key;
  };
  
  // Utility Function: getPersonaRole (used by GrandmaHelper)
  const getPersonaRole = (key: PersonaKey | string): string => {
    return PERSONA_MAP[key as PersonaKey]?.role || '';
  };


  return (
    <OscilloscopeContext.Provider value={{ state, dispatch, getPersonaName, getPersonaRole }}>
      {children}
    </OscilloscopeContext.Provider>
  );
};

export const useOscilloscope = () => {
  const context = useContext(OscilloscopeContext);
  if (!context) {
    // STATE CANONICALIZATION: Enforce strict usage
    throw new Error('useOscilloscope must be used within an OscilloscopeProvider');
  }
  return context;
};

import React, { createContext, useState, useCallback } from 'react';
import { useAIConsent } from '../../hooks/useAIConsent';

// Provide a centralized AI protection context so all components and hooks share the same consent/PII state.
export const AIProtectionContext = createContext<any>(null);

export const AIProtectionProvider: React.FC<{children: React.ReactNode}> = ({ children }) => {
  const { checkConsentAndExecute, isConsentModalOpen, dontShowAgain, setDontShowAgain, handleConfirm, handleCancel } = useAIConsent();
  const [isPiiModalOpen, setPiiModalOpen] = useState(false);
  const [piiMatches, setPiiMatches] = useState<Array<{ type: string; value: string }>>([]);
  const pendingActionRef: { current?: () => void } = { current: undefined };

  const handlePiiConfirm = useCallback(() => {
    if (pendingActionRef.current) pendingActionRef.current();
    setPiiModalOpen(false);
    pendingActionRef.current = undefined;
  }, []);

  const handlePiiCancel = useCallback(() => {
    setPiiModalOpen(false);
    pendingActionRef.current = undefined;
  }, []);

  const checkAndExecute = useCallback(async (input: string, fn: (safeInput: string) => Promise<any>) => {
    if (typeof input !== 'string' || input.trim().length === 0) {
      throw new Error('No input provided for AI prompt.');
    }
    // Simple PII pattern detection
    const email = /\b[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}\b/i;
    const ssn = /\b\d{3}-\d{2}-\d{4}\b/;
    const card = /\b(?:\d[ -]*){13,19}\b/;
    const matches: Array<{ type: string; value: string }> = [];
    const mEmail = input.match(email) || [];
    const mSsn = input.match(ssn) || [];
    const mCard = input.match(card) || [];
    mEmail.forEach(v => matches.push({ type: 'email', value: v }));
    mSsn.forEach(v => matches.push({ type: 'ssn', value: v }));
    mCard.forEach(v => matches.push({ type: 'credit_card', value: v }));

    if (matches.length > 0) {
      setPiiMatches(matches);
      pendingActionRef.current = () => checkConsentAndExecute(() => fn(input.trim()));
      setPiiModalOpen(true);
      return;
    }

    return checkConsentAndExecute(() => fn(input.trim()));
  }, [checkConsentAndExecute]);

  const value = { checkAndExecute, isPiiModalOpen, piiMatches, handlePiiConfirm, handlePiiCancel, isConsentModalOpen, handleConfirm, handleCancel, dontShowAgain, setDontShowAgain };
  return <AIProtectionContext.Provider value={value}>{children}</AIProtectionContext.Provider>;
};

export default AIProtectionProvider;

