                } catch (e) { /* ignore */ }
                // Extra debug: show the earlyInit and the seededState/view after merge
                try {
                    const earlyInitLog = typeof window !== 'undefined' ? (window as any).__WONKY_TEST_INITIALIZE__ : undefined;
                    // eslint-disable-next-line no-console
                    console.log('E2E: post-init debug', { earlyInit: earlyInitLog, seeded: seededState?.dashboardType, seededView: seededState?.view });
                } catch (e) { /* ignore */ }

                // Extra debug info to ensure the App that runs in E2E sees the
                // expected seeded view/dashboardType. This helps us verify the
                // final `appState.view` before it reaches `AppContent`.
                try {
                    // eslint-disable-next-line no-console
                    console.log('E2E: Returning E2E provider', {
                        isE2EMode: !!isE2EMode,
                        seededView: seededState?.view,
                        seededDashboard: seededState?.dashboardType,
                        testStateView: testState?.view,
                        testStateKeys: Object.keys(testState || {}),
                    });
                } catch (e) { /* ignore */ }
            return (
                <AppStateContext.Provider value={testContextValue}>
                    <ErrorBoundary>
                        {children}
                    </ErrorBoundary>
                </AppStateContext.Provider>
            );
    }

    const isTestMode = typeof window !== 'undefined' && (!!(window as any).__WONKY_E2E_TEST_MODE__ || (import.meta as any).env?.VITE_PLAYWRIGHT_ACCELERATE === 'true');
        const getPersonaDisplayName = (personaKey: string) => {
            try {
                if (!appState) return personaKey;
                if (!appState.personaOverrides) return personaKey;
                return appState.personaOverrides[personaKey] || personaKey;
            } catch (e) { return personaKey; }
        };
        const contextValue: AppContextType = { authUser, appState, dispatch: dispatchWrapper, isTestMode, getPersonaDisplayName };

  return (
    <AppStateContext.Provider value={contextValue}>
      {children}
    </AppStateContext.Provider>
  );
};

export const useAppState = () => {
    const context = useContext(AppStateContext);
    if (context === undefined) {
        throw new Error('useAppState must be used within an AppStateProvider');
    }
    return context;
};

import React, { createContext, useContext, useState, useEffect } from 'react';

type Flags = {
  aiEnabled: boolean;
};

const DEFAULTS: Flags = {
  aiEnabled: true,
};

const FeatureFlagsContext = createContext<{
  flags: Flags;
  setFlag: (k: keyof Flags, v: any) => void;
}>({ flags: DEFAULTS, setFlag: () => {} });

export const FeatureFlagsProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  // Read persisted user-level overrides from localStorage for now
  const [flags, setFlags] = useState<Flags>(() => {
    try {
      if (typeof window !== 'undefined') {
        const raw = window.localStorage.getItem('wonky_flags');
        if (raw) {
          return { ...DEFAULTS, ...JSON.parse(raw) };
        }
      }
    } catch (e) {
      console.warn('Error reading wonky_flags', e);
    }
    return DEFAULTS;
  });

  useEffect(() => {
    try {
      if (typeof window !== 'undefined') {
        window.localStorage.setItem('wonky_flags', JSON.stringify(flags));
      }
    } catch (e) {
      // ignore
    }
  }, [flags]);

  const setFlag = (k: keyof Flags, v: any) => {
    setFlags(prev => ({ ...prev, [k]: v }));
  };

  return <FeatureFlagsContext.Provider value={{ flags, setFlag }}>{children}</FeatureFlagsContext.Provider>;
};

export const useFeatureFlags = () => useContext(FeatureFlagsContext);
export const useAIEnabled = () => useFeatureFlags().flags.aiEnabled;

export default FeatureFlagsContext;

