import { sanitizePrompt } from '../utils/sanitizePrompt';

// --- LLM Model and API Configuration ---
// Using a simple proxy endpoint or direct call structure for clarity
const API_URL = "http://localhost:3000/api/gemini"; // Placeholder for production proxy/endpoint
const MODEL_NAME = 'gemini-2.5-flash-preview-09-2025';

// --- SYSTEM INSTRUCTIONS (Matching Persona Mandates) ---
const SYSTEM_INSTRUCTIONS = {
  // YIN / Stability
  grandma: "You are Margie Fay Katen (1925-2025), a former WWII riveter, homemaker, bowler, and matriarch from Oklahoma. Your tone is practical, loving, and firm. Advice must focus on structural/physical solutions, time management, or food. End with: 'Love, Grandma.'",
  grandpa: "You are Robert Katen, a stoic, highly detailed mechanic and systems tinkerer. Advice must focus on tools, maintenance, physics, or creating processes (SOPs). Be highly efficient and concise. End with: 'ΓÇöGrandpa.'",
  // YANG / Kinetic Energy
  bob: "You are Bob Haddock. Your energy is High Energy, fast-paced, motivational, and aggressive about *starting* tasks. Uses high-stim language. Advice is short and pushes for immediate physical movement. End with: 'ΓÇöBob. Go!'",
  marge: "You are Marge Watson, an empathetic organizer and planner. Your focus is on emotional regulation, breaking down complexity into visual/achievable steps, and nurturing resilience. Use soft, kind, and detailed language. End with: 'ΓÇöMarge, the Planner.'",
};

type PersonaKey = keyof typeof SYSTEM_INSTRUCTIONS;

// --- UTILITY ---
/**
 * Ensures the generated response text ends exactly with the required persona signature.
 */
function ensurePersonaEnding(text: string, personaKey: PersonaKey): string {
  const endings = {
    grandma: 'Love, Grandma.',
    grandpa: 'ΓÇöGrandpa.',
    bob: 'ΓÇöBob. Go!',
    marge: 'ΓÇöMarge, the Planner.',
  };
  const requiredEnding = endings[personaKey];
    
  // Normalize and clean up existing ending to prevent duplication (e.g., if model added its own punctuation)
  let cleanText = text.trim();
  if (cleanText.trim().endsWith(requiredEnding)) return cleanText;
    
  // If the text seems to end, remove final punctuation before appending (e.g., removes "." or "!")
  cleanText = cleanText.replace(/[\.\!\?]$/, '');
    
  return `${cleanText} ${requiredEnding}`;
}

/* eslint-disable no-await-in-loop */
const GEMINI_API_URL = 'https://generativeai.googleapis.com/v1/models';
const GEMINI_MODEL = process.env?.GEMINI_MODEL ?? 'gemini-2.5-flash-preview-09-2025';

function getApiKey(): string | undefined {
  const envAny: any = import.meta;
  return (envAny?.env && envAny.env.VITE_GEMINI_API_KEY) || (process.env as any).VITE_GEMINI_API_KEY;
}

interface GenerateOptions {
  prompt: string;
  systemInstruction?: string;
  maxTokens?: number;
}

export async function generateContent({ prompt, systemInstruction = '', maxTokens = 512 }: GenerateOptions): Promise<string> {
  // Prefer server-side proxy to keep API keys out of the client bundle
  const proxyEnabled = ((import.meta as any)?.env && (import.meta as any).env.VITE_GEMINI_USE_PROXY) !== 'false';
  const apiKey = getApiKey();
  // If proxy is enabled, we don't need a local apiKey (server keeps it secure).
  if (!proxyEnabled && !apiKey) {
    console.warn('VITE_GEMINI_API_KEY not set and proxy disabled; returning a polite failure');
    return 'Grandma is taking a nap. Try again later. Love, Grandma.';
  }

  // Use the local server-side proxy if possible. This endpoint is implemented in server.js
  const url = proxyEnabled ? '/api/gemini' : `${GEMINI_API_URL}/${GEMINI_MODEL}:generate`;
  const body = proxyEnabled ? {
    prompt,
    systemInstruction,
    maxTokens,
  } as any : {
    prompt: [
      { role: 'system', content: systemInstruction },
      { role: 'user', content: prompt },
    ],
    maxOutputTokens: maxTokens,
    temperature: 0.6,
  } as any;

  // Basic exponential backoff
  let attempt = 0;
  const maxAttempts = 3;
  let lastError: Error | null = null;

  while (attempt < maxAttempts) {
    try {
      // Abort requests after a reasonable period to avoid hanging clients
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 45_000);
      const headers: any = { 'Content-Type': 'application/json' };
      if (!proxyEnabled && apiKey) headers.Authorization = `Bearer ${apiKey}`;

      if (proxyEnabled) console.debug('geminiService: Using proxy at', url);
      const res = await fetch(url, {
        method: 'POST',
        headers,
        body: JSON.stringify(body),
        signal: controller.signal,
      });
      clearTimeout(timeoutId);

      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Gemini API error: ${res.status} ${res.statusText} - ${text}`);
      }

      const data = await res.json();
      // Convert whatever the shape is to a text response; be defensive
      if (data?.candidates && data.candidates.length > 0 && data.candidates[0].content) {
        return data.candidates[0].content;
      }

      if (data?.output?.text) {
        return data.output.text;
      }

      // Fallback: Try to string together any text segments
      const generated = JSON.stringify(data);
      return generated.substring(0, 10000);
    } catch (err: any) {
      lastError = err;
      attempt += 1;
      const delayMs = 2 ** attempt * 500;
      // eslint-disable-next-line no-await-in-loop
      await new Promise(resolve => setTimeout(resolve, delayMs));
      // If proxy is enabled and it's a network error, and we have a local API key, try direct API on last attempt
      if (proxyEnabled && apiKey && attempt >= maxAttempts) {
        try {
          // switch to direct Google API for a last-ditch attempt
          const directUrl = `${GEMINI_API_URL}/${GEMINI_MODEL}:generate`;
          const dController = new AbortController();
          const directRes = await fetch(directUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${apiKey}` },
            body: JSON.stringify({ prompt: [ { role: 'system', content: systemInstruction }, { role: 'user', content: prompt } ], maxOutputTokens: maxTokens, temperature: 0.6 }),
            signal: dController.signal,
          });
          if (directRes.ok) {
            const directData = await directRes.json();
            if (directData?.candidates && directData.candidates.length > 0 && directData.candidates[0].content) {
              return directData.candidates[0].content;
            }
            if (directData?.output?.text) {
              return directData.output.text;
            }
          }
        } catch (dErr) {
          lastError = dErr as Error;
        }
      }
    }
  }

  // If we reach here, all attempts failed
  console.warn('Gemini requests failed', lastError);
  return 'Grandma is having a little trouble connecting to the skyphones. Try again later. Love, Grandma.';
}

// Persona-specific wrapper
const GRANDMA_SYSTEM_INSTRUCTION = `You are Margie Fay Katen (1925-2025), a former WWII riveter, homemaker, bowler, and matriarch from Oklahoma. Your tone is practical, loving, and firm. Your advice is centered on solving physical or structural problems (like 'put your mind in order' or 'fix the tools'). Use short, concise sentences. DO NOT offer psychological platitudes. End every piece of advice with 'Love, Grandma.'`;

// GRANDPA MODE - friendly blunt, a retired carpenter with practical life tips and a no-nonsense tone
const GRANDPA_SYSTEM_INSTRUCTION = `You are William "Bill" Katen (1920-2025), a retired carpenter, gardener, and storyteller from Oklahoma. Your tone is gruff but kind, with a wry sense of humor and direct practical advice. Focus on actionable steps, simple metaphors, and tactile problem solving. Use concise sentences and end every piece of advice with 'Love, Grandpa.'`;

// BOB MODE - cheerful and pragmatic neighbor-type with practical small-hacks
const BOB_SYSTEM_INSTRUCTION = `You are Bob Haddock, a practical neighbor and friendly problem-solver. Your tone is upbeat, cheerful, and pragmatic. Provide small-world, down-to-earth hacks and practical next steps. Keep solutions clear and actionable. End with 'ΓÇöBob.'`;

// MARGE MODE - empathetic organizer who focuses on systems and emotional safety
const MARGE_SYSTEM_INSTRUCTION = `You are Marge Watson, a calm organizer and household systems expert. Your tone is gentle, supportive, and pragmatic. Provide step-by-step systems and low-stress first tasks. Keep it accessible and empathetic. End with 'ΓÇöMarge.'`;

export async function getGrandmaAdvice(userQuery: string): Promise<string> {
  const safeQuery = userQuery || '';
  const prompt = `${safeQuery}`;
  try {
    const output = await generateContent({ prompt, systemInstruction: GRANDMA_SYSTEM_INSTRUCTION, maxTokens: 512 });
    return output;
  } catch (e) {
    console.error('getGrandmaAdvice error', e);
    return 'Grandma is offline for a little while. Try again later. Love, Grandma.';
  }
}

export async function getGrandpaAdvice(userQuery: string): Promise<string> {
  const safeQuery = userQuery || '';
  const prompt = `${safeQuery}`;
  try {
    const output = await generateContent({ prompt, systemInstruction: GRANDPA_SYSTEM_INSTRUCTION, maxTokens: 512 });
    return output;
  } catch (e) {
    console.error('getGrandpaAdvice error', e);
    return 'Grandpa is taking a nap. Try again later. Love, Grandpa.';
  }
}

export async function getBobAdvice(userQuery: string): Promise<string> {
  const safeQuery = userQuery || '';
  const prompt = `${safeQuery}`;
  try {
    const output = await generateContent({ prompt, systemInstruction: BOB_SYSTEM_INSTRUCTION, maxTokens: 512 });
    return output;
  } catch (e) {
    console.error('getBobAdvice error', e);
    return 'Bob is taking a coffee break. Try again later. ΓÇöBob.';
  }
}

export async function getMargeAdvice(userQuery: string): Promise<string> {
  const safeQuery = userQuery || '';
  const prompt = `${safeQuery}`;
  try {
    const output = await generateContent({ prompt, systemInstruction: MARGE_SYSTEM_INSTRUCTION, maxTokens: 512 });
    return output;
  } catch (e) {
    console.error('getMargeAdvice error', e);
    return 'Marge is away for a moment. Try again later. ΓÇöMarge.';
  }
}

export default { generateContent, getGrandmaAdvice, getGrandpaAdvice, getBobAdvice, getMargeAdvice };

