interface Window {
  appState?: any;
  __PLAYWRIGHT_SKIP_DEV_BYPASS__?: boolean;
  __E2E_FORCE_VIEW__?: string;
  __WONKY_TEST_INITIALIZE__?: any;
  __WONKY_LAST_ERROR__?: any;
  __PLAYWRIGHT_ERROR_CAPTURE__?: boolean;
}

// Small memory TTL cache used for dev and single-instance server deployments.
// Note: In serverless environments this is short-lived. For production consider Redis or Memcached.
type CacheEntry<T> = { value: T; expiresAt: number };

class MemoryCache<T> {
  private store = new Map<string, CacheEntry<T>>();
  private ttlMs: number;
  constructor(ttlSeconds = 60) {
    this.ttlMs = ttlSeconds * 1000;
  }
  get(key: string): T | null {
    const e = this.store.get(key);
    if (!e) return null;
    if (Date.now() > e.expiresAt) {
      this.store.delete(key);
      return null;
    }
    return e.value;
  }
  set(key: string, value: T) {
    this.store.set(key, { value, expiresAt: Date.now() + this.ttlMs });
  }
  del(key: string) {
    this.store.delete(key);
  }
  clear() {
    this.store.clear();
  }
}

export default MemoryCache;

export function generateId(prefix?: string) {
  // If deterministic IDs are requested in the environment, honor that first
  try {
    if (typeof process !== 'undefined' && process.env && process.env.TEST_DETERMINISTIC_IDS === 'true') {
      const seed = Number(process.env.TEST_DETERMINISTIC_IDS_SEED || '1') || 1;
      if (!(globalThis as any).__WONKY_DET_ID_COUNTER__) (globalThis as any).__WONKY_DET_ID_COUNTER__ = { seed, i: 0 };
      const c = (globalThis as any).__WONKY_DET_ID_COUNTER__;
      c.i += 1;
      const body = `det-${seed}-${String(c.i).padStart(6, '0')}`;
      return prefix ? `${prefix}-${body}` : body;
    }
  } catch (e) {
    // ignore and fall through
  }

  try {
    if (typeof crypto !== 'undefined' && typeof (crypto as any).randomUUID === 'function') {
      const uuid = (crypto as any).randomUUID();
      return prefix ? `${prefix}-${uuid}` : uuid;
    }
  } catch (e) {
    // fall through to fallback
  }

  // If we're running in a test or PR mode, support a deterministic id generator
  // via TEST_DETERMINISTIC_IDS env var. Useful for vitest/playwright snapshots.
  // Deterministic check and crypto paths are handled above. Continue to fallback.

  // Fallback: Date + short random suffix
  const body = `${Date.now()}-${Math.random().toString(36).slice(2, 8)}`;
  return prefix ? `${prefix}-${body}` : body;
}

export default generateId;

// Global error capture utility. Registers handlers early in app startup
// to capture errors that happen before React error boundaries can catch them.
function registerGlobalErrorHandlers() {
  if (typeof window === 'undefined') return;

  // Avoid registering multiple times
  if ((window as any).__WONKY_GLOBAL_ERROR_HANDLERS__) return;
  (window as any).__WONKY_GLOBAL_ERROR_HANDLERS__ = true;

  // Save errors to localStorage for E2E capture
  const saveError = (message: string, stack: string | undefined) => {
    if (typeof window !== 'undefined') {
      try {
        const payload = JSON.stringify({ message, stack, ts: new Date().toISOString() });
        window.localStorage.setItem('wonky-last-error', payload);
      } catch (e) {
        // ignore
      }
    }
  };

  if (typeof window !== 'undefined') {
    window.onerror = function (message, source, lineno, colno, error) {
      // eslint-disable-next-line no-console
      console.error('GLOBAL_ONERROR', message, { source, lineno, colno, stack: error?.stack });
      saveError(String(message), error?.stack);
    };

    // Catch unhandled promise rejections
    window.onunhandledrejection = function (event: any) {
      // eslint-disable-next-line no-console
      console.error('GLOBAL_UNHANDLEDREJECTION', event?.reason);
      const reason = event?.reason;
      saveError(reason?.message || String(reason), reason?.stack);
    };

    // Hook console.error to also write the last-known error for E2E
    const origConsoleError = console.error.bind(console);
    console.error = (...args: any[]) => {
      try {
        const msg = args.map(a => (a && a.stack) || String(a)).join(' ');
        saveError(msg, (args[0] && args[0].stack) || undefined);
      } catch (e) {
        origConsoleError(...args);
      }
      origConsoleError(...args);
    };
  }
      // ignore
    }
    origConsoleError(...args);
  };
}

export default registerGlobalErrorHandlers;
