// Utilities for modules: topological sort, cycle detection for dependency resolution
export type Graph<T> = Map<T, Set<T>>;

export function buildDependencyGraph<T extends string>(nodes: T[], edges: Record<T, T[] | undefined>): Graph<T> {
  const graph: Graph<T> = new Map();
  nodes.forEach((n) => graph.set(n, new Set()));
  Object.entries(edges).forEach(([from, toList]) => {
    if (!toList) return;
    toList.forEach((to) => {
      if (!graph.has(from as T)) graph.set(from as T, new Set());
      graph.get(from as T)!.add(to as T);
    });
  });
  return graph;
}

export function topologicalSort<T extends string>(graph: Graph<T>): T[] {
  const inDegree = new Map<T, number>();
  graph.forEach((deps, node) => inDegree.set(node, 0));
  graph.forEach((deps) => {
    deps.forEach((to) => inDegree.set(to, (inDegree.get(to) || 0) + 1));
  });
  const queue: T[] = [];
  inDegree.forEach((deg, node) => {
    if (deg === 0) queue.push(node);
  });
  const result: T[] = [];
  while (queue.length) {
    const n = queue.shift()!;
    result.push(n);
    graph.get(n)!.forEach((m) => {
      const newDeg = (inDegree.get(m) || 0) - 1;
      inDegree.set(m, newDeg);
      if (newDeg === 0) queue.push(m);
    });
  }
  if (result.length !== graph.size) {
    throw new Error('Cyclic dependency detected in modules');
  }
  return result;
}

export function sanitizePrompt(text: string) {
  if (!text) return text;
  // Remove emails
  let s = text.replace(/\b[\w.%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}\b/g, '[REDACTED_EMAIL]');
  // Remove phone numbers
  s = s.replace(/\+?\d[\d\-\s()]{7,}\d/g, '[REDACTED_PHONE]');
  // Remove obvious PII (SSN-like patterns)
  s = s.replace(/\b\d{3}-\d{2}-\d{4}\b/g, '[REDACTED_SSN]');
  // Remove child names: simple example list ΓÇö in production use a configurable list
  s = s.replace(/\bWillow\b|\bSebastian\b/gi, '[REDACTED_NAME]');
  return s;
}

export default { sanitizePrompt };

